import {
    ChakraProvider,
    Input,
    Box,
    Center,
    CircularProgress,
    CircularProgressLabel,
    Text
} from '@chakra-ui/react'
import { useState, useEffect } from 'react';
import { Botão } from '../components/Button';
import { login } from '../services/login';
import {api} from '../api'

interface UserData {
    email: string
    password: string
    name: string
}

export const Card = () => {
    const [email, setEmail] = useState<string>('')
    const [userData, setUserData] = useState<null | UserData>()

    useEffect(() =>{
        const getData = async () => {
            const data: any | UserData = await api
            setUserData(data)
        }
        getData()
    }, []) //parar função async

    console.log(userData)

    return (
        <ChakraProvider>
            {
            userData === null || userData === undefined ?
            <Center><CircularProgress paddingTop= '15px' color='black' isIndeterminate value={80} /></Center> :
            <Center paddingTop='15px' color ='black'>Informações carregadas!</Center> 
            }
            <Center padding='30px'>
                <Box backgroundColor='white' borderRadius='25px' padding='25px'>
                    <Center paddingEnd='15px'> <Text as='b' color='black'>Faça o login:</Text></Center>
                    
                    <Input borderRadius='25px' placeholder="email" value={email} onChange={(event) => setEmail(event.target.value)} />
                    <Input borderRadius='25px' placeholder="password" />
                    <Center paddingTop='15px'>
                        <Botão onClick={() => login(email)} />
                    </Center>
                </Box>
            </Center>
        </ChakraProvider>
    )
}
