import { Box, Center, Heading, Input } from "@chakra-ui/react";
import { useState } from "react";
import { Botão } from "../components/Button";
import { Card } from "../components/Card";
import { Header } from "../components/Header";
import { Layout } from "../components/Layout";
import { login } from "../services/login";

const Home = () => {
    const [email, setEmail] = useState<string>('')
    return (            
        <Card>
            <Center padding='30px'>
                <Box backgroundColor='white' borderRadius='25px' padding='25px'>
                    <Box padding='10px'> <Heading as='b' color='black'>Faça o login:</Heading></Box>

                    <Input borderRadius='25px' placeholder="email" value={email} onChange={(event) => setEmail(event.target.value)} />
                    <Input borderRadius='25px' placeholder="password" />
                    <Center paddingTop='15px'>
                        <Botão onClick={() => login(email)} />
                    </Center>
                </Box>
            </Center>
        </Card>
    )
}

export default Home;
