import { Center, SimpleGrid, Spinner } from "@chakra-ui/react"
import { useEffect, useState } from "react"
import { api } from "../api"
import CardInfo from "../components/CardInfo"

interface UserData {
    email: string
    password: string
    name: string,
    balance: number
}

const Conta = () =>{
    const [userData, setUserData] = useState<null | UserData>()

    useEffect(() =>{
        const getData = async () => {
            const data: any | UserData = await api
            setUserData(data)
        }
        getData()
    }, []) //parar função async

    const actualData = new Date()

    console.log(userData?.name)
    return(
        <Center>
        <SimpleGrid columns={2} spacing={8} paddingTop='16px'>
            {
                userData === null || userData === undefined
                ? <Center><Spinner size = 'xl' color = 'white'/></Center>
                :  <CardInfo mainContent = {`Bem vindo ${userData?.name}`} content={`${actualData?.getDay()} / ${actualData?.getMonth()} / ${actualData?.getFullYear()} / ${actualData?.getHours()}:${actualData?.getMinutes()}`}/>
            }
       
        </SimpleGrid>
        </Center>
    )
}

export default Conta
